mapper.select:
SELECT t.* FROM user_inf t 
WHERE 1=1 

 	AND t.id=#{id}


 	AND t.loginname=#{loginname}


 	AND t.password=#{password}


 	AND t.status=#{status}


 	AND t.createdate=#{createdate}


 	AND t.username=#{username}

ORDER BY t.createTime ASC

mapper.delete:
	DELETE FROM user_inf  
	WHERE 

        id=#{id}


        goodId=#{goodId}

DELETE from user_inf 
WHERE id in(

		#{o.id}

	)
