mapper.select:
SELECT t.* FROM user_inf t where 1=1

 	AND t.id=#{id}


 	AND t.loginname=#{loginname}


 	AND t.password=#{password}


 	AND t.status=#{status}


 	AND t.createdate=#{createdate}


 	AND t.username=#{username}

ORDER BY t.id ASC

SELECT t.* FROM user_inf t where 1=1

 	AND t.id = #{id}


 	AND t.loginname like '%#{loginname}%'


 	AND t.password=#{password}


 	AND t.status=#{status}


 	AND t.createdate=#{createdate}


 	AND t.username like '%#{username}%'

ORDER BY t.id ASC

mapper.delete:
DELETE from user_inf 
WHERE id in(
	
		#{o.id}
	
	)
